function calcValues(sg,vg){"use strict";var i,res=[],s=sg-vg;for(i=-2;3>i;i+=1)s=sg-vg+10*i,res.push([s,50-s,75-s,90-s,100-s]);return res}function calcTable(sg,vg,sk){"use strict";var i,j,table=document.getElementById("table"),res=calcValues(sg,vg,sk),s="<table><tr><td>Diff</td><td>+</td><td>+E</td><td>+G</td><td>+K</td></tr>";for(i=0;i<res.length;++i){for(s+="<tr>",j=0;j<res[i].length;++j)s+="<td>"+res[i][j]+"</td>";s+="</tr>"}s+="</table>",table.innerHTML=s}function calculate(){"use strict";calcTable(document.getElementById("KG").value,document.getElementById("VG").value,document.getElementById("SK").value)}function processValues(el1,el2){"use strict";document.getElementById(el1).value=document.getElementById(el2).value,calculate()}function onStart(){"use strict";document.getElementById("KGtext").value=document.getElementById("KG").value,document.getElementById("VGtext").value=document.getElementById("VG").value,document.getElementById("SKtext").value=document.getElementById("SK").value,calculate()}